mapper.select:
SELECT /* 인적사항 */ PF.USER_ID AS USER_ID, PF.USER_NM AS USER_NM, PF.USER_POSITION AS USER_POSITION, (SELECT CODE_VALUE FROM TB_COMMON_CODE WHERE CODE_GROUP_ID = 'PSIT' AND CODE_ID = PF.USER_POSITION) AS USER_POSITION_NM, PF.USER_BIRTH AS USER_BIRTH, PF.USER_DEPARTMENT AS USER_DEPARTMENT, (SELECT CODE_VALUE FROM TB_COMMON_CODE WHERE CODE_GROUP_ID = 'ORG' AND CODE_ID = PF.USER_DEPARTMENT) AS USER_DEPARTMENT_NM, PF.HIRE_DATE AS HIRE_DATE, PF.USER_PHONE AS USER_PHONE, PF.USER_ADDRESS AS USER_ADDRESS, PF.EDU1_SCHOOL_NAME AS EDU1_SCHOOL_NAME, PF.EDU1_GRAD_STATUS AS EDU1_GRAD_STATUS, PF.EDU1_START_DATE AS EDU1_START_DATE, PF.EDU1_END_DATE AS EDU1_END_DATE, PF.EDU1_GUBUN AS EDU1_GUBUN, PF.EDU2_SCHOOL_NAME AS EDU2_SCHOOL_NAME, PF.EDU2_GRAD_STATUS AS EDU2_GRAD_STATUS, PF.EDU2_START_DATE AS EDU2_START_DATE, PF.EDU2_END_DATE AS EDU2_END_DATE, PF.EDU2_GUBUN AS EDU2_GUBUN, PF.EDU3_SCHOOL_NAME AS EDU3_SCHOOL_NAME, PF.EDU3_GRAD_STATUS AS EDU3_GRAD_STATUS, PF.EDU3_START_DATE AS EDU3_START_DATE, PF.EDU3_END_DATE AS EDU3_END_DATE, PF.EDU3_GUBUN AS EDU2_GUBUN, PF.MAJOR AS MAJOR, PF.DOUBLE_MAJOR AS DOUBLE_MAJOR, PF.TOTAL_GRADE AS TOTAL_GRADE, PF.STANDARD_GRADE AS STANDARD_GRADE,
/* 파일 */ AT.FILE_ORI_NM AS FILE_ORI_NM, AT.FILE_SVER_NM AS FILE_SVER_NM, AT.FILE_SVER_PATH AS FILE_SVER_PATH, AT.FILE_EXTENSION AS FILE_EXTENSION
FROM TB_USER_PROFILE_INFO PF JOIN TB_ATTACHMENT_INFO AT ON PF.USER_ID = AT.USER_ID AND PF.FILE_SEQ = AT.FILE_SEQ AND AT.USE_YN = 'Y' WHERE PF.USER_ID = #{user_id} AND PF.USE_YN = 'Y'
