mapper.insert:
INSERT INTO TB_USER_SKILL_INFO_HIST(USER_ID, PROJECT_SEQ, SKILL_ID, HIST_SEQ, SKILL_NM, CREATED_DATE, CREATOR)
SELECT user_id,
       project_seq,
       skill_id,
       #{hist_seq},
       skill_nm,
       now(),
       #{user_id}
FROM TB_USER_SKILL_INFO
WHERE USER_ID = #{user_id}
  AND PROJECT_SEQ = #{project_seq}
  AND SKILL_ID = #{skill_id}
