mapper.select:
SELECT COUNT(DISTINCT PROJECT_NM)
FROM TB_PROJECT_INFO
WHERE 1=1

    AND project_start_date >= #{project_start_date}


    AND project_end_date <= #{project_end_date}


    AND now() between project_start_date and project_end_date

SELECT project_nm as project_nm, project_client, min(project_start_date) as project_start_date, max(project_end_date) as project_end_date FROM TB_PROJECT_INFO WHERE 1=1
AND project_start_date >= #{project_start_date}
AND project_end_date <= #{project_end_date}
AND now() between project_start_date and project_end_date
GROUP BY project_nm, project_client ORDER by project_start_date desc limit #{limit} offset #{offset}

SELECT DISTINCT pf.user_nm, pf.user_department_nm, pf.user_position_nm, pf.project_totalMonth, MIN(pj.project_start_date) AS project_start_date, MAX(pj.project_end_date) AS project_end_date FROM TB_PROJECT_INFO pj JOIN (SELECT up.user_id, up.user_nm, cc_org.code_value AS user_department_nm, cc_psit.code_value AS user_position_nm, calculate_total_months_v2(up.user_id) AS project_totalMonth FROM TB_USER_PROFILE_INFO up JOIN TB_COMMON_CODE cc_org ON up.user_department = cc_org.code_id AND cc_org.code_group_id = 'ORG' JOIN TB_COMMON_CODE cc_psit ON up.user_position = cc_psit.code_id AND cc_psit.code_group_id = 'PSIT' ) pf ON pj.user_id = pf.user_id WHERE pj.project_nm = #{project_nm}
AND pj.project_start_date >= #{project_start_date}
AND pj.project_end_date <= #{project_end_date}
GROUP BY pf.user_nm, pf.user_department_nm, pf.user_position_nm, pf.project_totalMonth ORDER BY project_start_date DESC
